import React from 'react';
import './Board.css';
import { getTetromino, TETROMINO_COLORS } from '../utils/tetrominos';

const ROWS = 20;
const COLS = 10;

// Génère une matrice vide
const emptyBoard = Array.from({ length: ROWS }, () => Array(COLS).fill(0));

export default function Board({ board = emptyBoard }) {
  return (
    <div className="tetris-board">
      {board.map((row, y) =>
        row.map((cell, x) => (
          <div
            key={`${y}-${x}`}
            className={`cell cell-${cell}`}
          />
        ))
      )}
    </div>
  );
}

// Next piece preview component
export function NextPiecePreview({ type }) {
  if (!type) return null;
  const { shape, color } = (() => {
    const tetro = getTetromino(type);
    return {
      shape: tetro.shape,
      color: TETROMINO_COLORS[type]
    };
  })();
  // Find bounding box of the shape
  let minY = 4, maxY = -1, minX = 4, maxX = -1;
  for (let y = 0; y < shape.length; y++) {
    for (let x = 0; x < shape[y].length; x++) {
      if (shape[y][x]) {
        if (y < minY) minY = y;
        if (y > maxY) maxY = y;
        if (x < minX) minX = x;
        if (x > maxX) maxX = x;
      }
    }
  }
  // Calculate offset to center the piece
  const pieceHeight = maxY - minY + 1;
  const pieceWidth = maxX - minX + 1;
  const offsetY = Math.floor((4 - pieceHeight) / 2) - minY;
  const offsetX = Math.floor((4 - pieceWidth) / 2) - minX;
  // Build the 4x4 grid with the piece centered
  const grid = Array.from({ length: 4 }, (_, y) =>
    Array.from({ length: 4 }, (_, x) => {
      const sy = y - offsetY;
      const sx = x - offsetX;
      return (shape[sy] && shape[sy][sx]) ? color : null;
    })
  );
  return (
    <div style={{
      display: 'grid',
      gridTemplateRows: 'repeat(4, 18px)',
      gridTemplateColumns: 'repeat(4, 18px)',
      gap: 2,
      alignItems: 'center',
      justifyItems: 'center',
      margin: '0 auto',
      width: 4 * 18 + 3 * 2,
      height: 4 * 18 + 3 * 2,
    }}>
      {grid.flat().map((cell, i) => (
        <div key={i} style={{
          width: 18,
          height: 18,
          background: cell || 'transparent',
          borderRadius: 3,
          border: cell ? '1px solid #fff8' : 'none',
          boxSizing: 'border-box',
          transition: 'background 0.2s',
        }} />
      ))}
    </div>
  );
}

import React from 'react';

const controls = [
  { icon: '⬅️➡️', text: 'Move piece left/right' },
  { icon: '🔄', text: 'Rotate piece' },
  { icon: '⬇️', text: 'Soft drop' },
  { icon: '⏬', text: 'Hard drop' },
];

const Controls = () => (
  <div className="controls enhanced fade-in">
    <h3>Controls</h3>
    <ul className="controls-list">
      {controls.map((ctrl, idx) => (
        <li key={idx} className="controls-list-item">
          <span className="control-icon" aria-hidden="true">{ctrl.icon}</span>
          <span className="control-text">{ctrl.text}</span>
        </li>
      ))}
    </ul>
  </div>
);

export default Controls;

import React from 'react';

const CreateRoomModal = ({
  isOpen,
  onClose,
  createRoomId,
  username,
  setUsername,
  currentRoomId,
  isRoomLeader,
  roomPlayers,
  onValidateUsername,
  onStartGame,
  onLeaveRoom,
  joinError
}) => {
  if (!isOpen) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100vw',
      height: '100vh',
      background: 'rgba(0,0,0,0.85)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 100
    }}>
      <div style={{
        background: '#222',
        padding: '40px 32px 32px 32px',
        borderRadius: 24,
        boxShadow: '0 4px 32px #000a',
        textAlign: 'center',
        color: '#FFD700',
        minWidth: 340,
        minHeight: 320,
        position: 'relative'
      }}>
        <div style={{fontSize: 30, fontWeight: 900, marginBottom: 12, letterSpacing: 2}}>
          Créer une Room
        </div>

        <div style={{
          fontSize: 14, 
          color: '#4CAF50', 
          marginBottom: 18, 
          padding: '8px 12px',
          backgroundColor: 'rgba(76, 175, 80, 0.1)',
          borderRadius: 8,
          border: '1px solid rgba(76, 175, 80, 0.3)'
        }}>
          👑 Vous serez le leader et pourrez lancer le jeu pour tous les joueurs
        </div>

        <div style={{fontSize: 18, color: '#fff', marginBottom: 10}}>
          ID de la Room : <span style={{color: '#FFD700', fontWeight: 'bold'}}>{createRoomId}</span>
        </div>

        {joinError && (
          <div style={{color: '#ff4444', fontSize: 16, marginBottom: 10, fontWeight: 'bold'}}>
            {joinError}
          </div>
        )}

        {/* Show username input and validate button only if room is not created yet */}
        {!currentRoomId && (
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 8, marginBottom: 18}}>
            <input
              type="text"
              placeholder="Entrez votre nom"
              value={username}
              onChange={e => setUsername(e.target.value)}
              style={{
                padding: '10px',
                fontSize: 18,
                borderRadius: 8,
                border: '1px solid #FFD700',
                outline: 'none',
                color: '#222',
                background: '#fff',
                flex: 1,
                minWidth: 200
              }}
              onKeyDown={e => { if (e.key === 'Enter') onValidateUsername(); }}
            />
            <button
              onClick={onValidateUsername}
              style={{
                padding: '10px 16px',
                fontSize: 16,
                borderRadius: 8,
                background: '#28a745',
                color: '#fff',
                border: 'none',
                fontWeight: 'bold',
                cursor: 'pointer',
                whiteSpace: 'nowrap'
              }}
            >Valider</button>
          </div>
        )}

        {/* Show room status if already created */}
        {currentRoomId && (
          <div style={{
            fontSize: 16, 
            color: '#4CAF50', 
            marginBottom: 18, 
            padding: '12px',
            backgroundColor: 'rgba(76, 175, 80, 0.1)',
            borderRadius: 8,
            border: '1px solid rgba(76, 175, 80, 0.3)',
            textAlign: 'center'
          }}>
            ✅ Room créée avec succès ! En attente d'autres joueurs...
            <div style={{marginTop: 12, fontSize: 14, color: '#fff'}}>
              <strong>Lien à partager :</strong>
              <div style={{
                marginTop: 8,
                padding: '8px 12px',
                backgroundColor: '#2a2a2a',
                borderRadius: 6,
                fontFamily: 'monospace',
                fontSize: 13,
                color: '#FFD700',
                wordBreak: 'break-all',
                cursor: 'pointer',
                border: '1px solid #555'
              }}
              onClick={() => {
                const link = `${window.location.origin}/#${currentRoomId}[PLAYER_NAME]`;
                navigator.clipboard.writeText(link);
                // Show a temporary feedback
                const elem = document.createElement('div');
                elem.textContent = 'Lien copié !';
                elem.style.cssText = 'position:fixed;top:50%;left:50%;transform:translate(-50%,-50%);background:#4CAF50;color:white;padding:12px 24px;border-radius:8px;z-index:10000;font-weight:bold;';
                document.body.appendChild(elem);
                setTimeout(() => document.body.removeChild(elem), 2000);
              }}>
                {`${window.location.origin}/#${currentRoomId}[PLAYER_NAME]`}
              </div>
              <div style={{fontSize: 12, color: '#ccc', marginTop: 4}}>
                Cliquer pour copier
              </div>
            </div>
          </div>
        )}

        {/* Show players list if room exists */}
        {currentRoomId && roomPlayers.length > 0 && (
          <div style={{
            marginBottom: 18,
            padding: 12,
            backgroundColor: '#1a1a1a',
            borderRadius: 8,
            border: '1px solid #333'
          }}>
            <div style={{fontSize: 16, color: '#FFD700', marginBottom: 8, fontWeight: 'bold'}}>
              Joueurs connectés ({roomPlayers.length}) :
            </div>
            <ul style={{listStyle: 'none', padding: 0, margin: 0, fontSize: 14}}>
              {roomPlayers.map((player, index) => (
                <li key={index} style={{
                  padding: '4px 8px',
                  borderBottom: index < roomPlayers.length - 1 ? '1px solid #444' : 'none',
                  color: player.isLeader ? '#FFD700' : '#fff'
                }}>
                  {player.name} {player.isLeader && '👑 (Leader)'}
                </li>
              ))}
            </ul>
          </div>
        )}

        {/* Show START GAME button only if already in a room and is leader */}
        {currentRoomId && isRoomLeader && (
          <button
            onClick={onStartGame}
            style={{
              padding: '12px 32px',
              fontSize: 20,
              borderRadius: 10,
              background: '#ff6600',
              color: '#fff',
              border: 'none',
              fontWeight: 'bold',
              cursor: 'pointer',
              boxShadow: '0 2px 8px #0006',
              letterSpacing: 1,
              marginBottom: 10
            }}
          >START GAME</button>
        )}

        {/* The room creation happens automatically when validating username */}
        <button
          onClick={onLeaveRoom}
          style={{
            position: 'absolute',
            top: 12,
            right: 18,
            background: 'transparent',
            color: '#FFD700',
            border: 'none',
            fontSize: 28,
            cursor: 'pointer',
            fontWeight: 'bold',
          }}
          title="Fermer"
        >×</button>
      </div>
    </div>
  );
};

export default CreateRoomModal;

import React from 'react';

const GameControls = ({
  playing,
  paused,
  onTogglePause,
  onQuit
}) => {
  if (!playing) return null;

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      gap: 6,
      marginTop: 12
    }}>
      <button
        onClick={onTogglePause}
        style={{
          width: 120,
          height: 28,
          borderRadius: 8,
          border: 'none',
          background: paused ? '#4CAF50' : '#FF9800',
          color: 'white',
          fontWeight: 'bold',
          fontSize: 12,
          cursor: 'pointer',
          transition: 'all 0.2s ease',
          boxShadow: '0 2px 8px rgba(0,0,0,0.3)'
        }}
      >
        {paused ? '▶ REPRENDRE' : '⏸ PAUSE'}
      </button>

      <button
        onClick={onQuit}
        style={{
          width: 120,
          height: 28,
          borderRadius: 8,
          border: 'none',
          background: '#f44336',
          color: 'white',
          fontWeight: 'bold',
          fontSize: 12,
          cursor: 'pointer',
          transition: 'all 0.2s ease',
          boxShadow: '0 2px 8px rgba(0,0,0,0.3)'
        }}
      >
        🚪 QUITTER
      </button>
    </div>
  );
};

export default GameControls;

import React from 'react';
import { NextPiecePreview } from './Board';

const GameHUD = ({
  score,
  lines,
  nextType,
  playing,
  paused,
  isMultiplayer,
  currentPlayerId,
  onTogglePause,
  onQuit,
  updateHighScores,
  updateGameStats,
  socketService
}) => {
  const handleQuit = () => {
    // Save score and stats before quitting
    if (score > 0 || lines > 0) {
      updateHighScores(score);
      updateGameStats(score, lines, isMultiplayer, false);
    }

    // If in multiplayer, send game-over to server to eliminate player
    if (isMultiplayer && currentPlayerId) {
      socketService.sendGameOver(currentPlayerId);
    }

    // Call the onQuit callback
    onQuit();
  };

  return (
    <div style={{margin: 24, display: 'flex', flexDirection: 'column', gap: 48, zIndex: 2}}>
      {/* Score/Level/Lines Panel */}
      <div style={{
        width: 160, 
        height: 160, 
        background: '#222', 
        borderRadius: 18, 
        boxShadow: '0 2px 12px #0008', 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'flex-start', 
        padding: 16
      }}>
        <div style={{fontWeight: 'bold', fontSize: 22, marginBottom: 8}}>SCORE</div>
        <div style={{
          background: '#111', 
          borderRadius: 8, 
          width: 120, 
          height: 32, 
          marginBottom: 8, 
          color: '#FFD700', 
          fontSize: 20, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center'
        }}>
          {score}
        </div>
        <div style={{fontWeight: 'bold', fontSize: 18, marginBottom: 4}}>LEVEL</div>
        <div style={{
          background: '#111', 
          borderRadius: 8, 
          width: 120, 
          height: 24, 
          marginBottom: 8, 
          color: '#FFD700', 
          fontSize: 16, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center'
        }}>
          1
        </div>
        <div style={{fontWeight: 'bold', fontSize: 18, marginBottom: 4}}>LINES</div>
        <div style={{
          background: '#111', 
          borderRadius: 8, 
          width: 120, 
          height: 24, 
          color: '#FFD700', 
          fontSize: 16, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center'
        }}>
          {lines}
        </div>
      </div>

      {/* Next Panel en bas du score */}
      <div style={{
        width: 160, 
        height: playing ? 180 : 120, 
        background: '#222', 
        borderRadius: 18, 
        boxShadow: '0 2px 12px #0008', 
        display: 'flex', 
        flexDirection: 'column', 
        alignItems: 'center', 
        justifyContent: 'flex-start', 
        padding: 12
      }}>
        <div style={{fontWeight: 'bold', fontSize: 22, marginBottom: 8}}>NEXT</div>
        <div style={{
          background: '#111', 
          borderRadius: 8, 
          width: 120, 
          height: 80, 
          marginBottom: playing ? 12 : 0, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center'
        }}>
          <NextPiecePreview type={nextType} />
        </div>

        {/* Boutons Pause et Quit - seulement quand on joue */}
        {playing && (
          <>
            <button
              onClick={onTogglePause}
              style={{
                width: 120,
                height: 28,
                borderRadius: 8,
                border: 'none',
                background: paused ? '#4CAF50' : '#FF9800',
                color: 'white',
                fontWeight: 'bold',
                fontSize: 12,
                cursor: 'pointer',
                transition: 'all 0.2s ease',
                boxShadow: '0 2px 8px rgba(0,0,0,0.3)',
                marginBottom: 6
              }}
            >
              {paused ? '▶ REPRENDRE' : '⏸ PAUSE'}
            </button>

            <button
              onClick={handleQuit}
              style={{
                width: 120,
                height: 28,
                borderRadius: 8,
                border: 'none',
                background: '#f44336',
                color: 'white',
                fontWeight: 'bold',
                fontSize: 12,
                cursor: 'pointer',
                transition: 'all 0.2s ease',
                boxShadow: '0 2px 8px rgba(0,0,0,0.3)'
              }}
            >
              🚪 QUITTER
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default GameHUD;

import React from 'react';

const getInitials = (name) => name.split(' ').map(n => n[0]).join('').toUpperCase();

const GameLobby = ({ players = [], roomId = '', isLeader = false, onStartGame }) => {
  const handleCopyRoom = () => {
    navigator.clipboard.writeText(roomId);
  };
  return (
    <div className="game-lobby">
      <div className="lobby-icon" aria-label="Lobby">
        <span role="img" aria-label="lobby" style={{fontSize: '2.5rem'}}>🎮</span>
      </div>
      <h2>Game Lobby</h2>
      <div className="room-id-row">
        <p>Room: <strong className="room-id">{roomId}</strong></p>
        <button className="copy-room-btn" onClick={handleCopyRoom} title="Copy room code">📋</button>
      </div>
      <h3>Players</h3>
      <ul className="player-list">
        {players.map((player) => (
          <li key={player.id} className="player-list-item">
            <span className="player-avatar">{getInitials(player.name)}</span>
            <span className="player-name">{player.name}</span>
            {player.isLeader && <span className="leader-badge">👑</span>}
          </li>
        ))}
      </ul>
      {isLeader && (
        <button className="start-game-btn animated" onClick={onStartGame}>Start Game</button>
      )}
    </div>
  );
};

export default GameLobby;

import React from 'react';

const JoinRoomModal = ({
  isOpen,
  onClose,
  waitingForRematch,
  joinRoomId,
  setJoinRoomId,
  joinUsername,
  setJoinUsername,
  joinError,
  currentRoomId,
  isJoiningRoom,
  roomPlayers,
  isRoomLeader,
  onJoinRoom,
  onStartGame
}) => {
  if (!isOpen) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.8)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: '#1a1a1a',
        border: '3px solid #FFD700',
        borderRadius: 15,
        padding: 24,
        minWidth: 350,
        maxWidth: 500,
        color: '#fff',
        position: 'relative',
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.5)'
      }}>
        <h2 style={{
          margin: '0 0 12px 0',
          color: '#FFD700',
          textAlign: 'center',
          fontSize: 24,
          fontWeight: 'bold'
        }}>
          {waitingForRematch ? 'EN ATTENTE DU REMATCH' : 'REJOINDRE UNE ROOM'}
        </h2>

        {waitingForRematch ? (
          <div style={{
            fontSize: 16, 
            color: '#4CAF50', 
            marginBottom: 20, 
            padding: '12px',
            backgroundColor: 'rgba(76, 175, 80, 0.1)',
            borderRadius: 8,
            border: '1px solid rgba(76, 175, 80, 0.3)',
            textAlign: 'center'
          }}>
            🔄 Prêt pour le rematch ! En attente que le leader lance la nouvelle partie...
          </div>
        ) : (
          <div style={{
            fontSize: 14, 
            color: '#ff9800', 
            marginBottom: 20, 
            padding: '8px 12px',
            backgroundColor: 'rgba(255, 152, 0, 0.1)',
            borderRadius: 8,
            border: '1px solid rgba(255, 152, 0, 0.3)',
            textAlign: 'center'
          }}>
            Seul le leader de la room peut lancer le jeu
          </div>
        )}

        <div style={{
          fontSize: 13, 
          color: '#00bcd4', 
          marginBottom: 15, 
          padding: '8px 12px',
          backgroundColor: 'rgba(0, 188, 212, 0.1)',
          borderRadius: 8,
          border: '1px solid rgba(0, 188, 212, 0.3)',
          textAlign: 'center'
        }}>
          💡 <strong>Astuce :</strong> Vous pouvez rejoindre directement via l'URL<br/>
          <code style={{fontSize: 11, fontFamily: 'monospace'}}>
            {window.location.origin}/#ROOM123[VotreNom]
          </code>
        </div>

        {/* Show room ID input only if not in a room yet */}
        {!currentRoomId && (
          <div style={{ marginBottom: 15 }}>
            <label style={{
              display: 'block',
              marginBottom: 8,
              color: '#FFD700',
              fontWeight: 'bold'
            }}>ID DE LA ROOM:</label>
            <input
              type="text"
              value={joinRoomId}
              onChange={(e) => setJoinRoomId(e.target.value.toUpperCase())}
              placeholder="Entrez l'ID de la room"
              style={{
                width: '100%',
                padding: 10,
                border: '2px solid #333',
                borderRadius: 8,
                backgroundColor: '#2a2a2a',
                color: '#fff',
                fontSize: 16,
                outline: 'none'
              }}
              onKeyDown={e => { if (e.key === 'Enter') onJoinRoom(); }}
            />
          </div>
        )}

        {/* Show username input and join button only if not in a room yet */}
        {!currentRoomId && (
          <div style={{ marginBottom: 20 }}>
            <label style={{
              display: 'block',
              marginBottom: 8,
              color: '#FFD700',
              fontWeight: 'bold'
            }}>VOTRE NOM:</label>
            <div style={{ display: 'flex', gap: 10 }}>
              <input
                type="text"
                value={joinUsername}
                onChange={(e) => setJoinUsername(e.target.value)}
                placeholder="Entrez votre nom"
                style={{
                  flex: 1,
                  padding: 10,
                  border: '2px solid #333',
                  borderRadius: 8,
                  backgroundColor: '#2a2a2a',
                  color: '#fff',
                  fontSize: 16,
                  outline: 'none'
                }}
                onKeyDown={e => { if (e.key === 'Enter') onJoinRoom(); }}
              />
              <button
                onClick={onJoinRoom}
                disabled={!joinRoomId.trim() || !joinUsername.trim()}
                style={{
                  padding: '10px 20px',
                  backgroundColor: '#28a745',
                  color: '#fff',
                  border: 'none',
                  borderRadius: 8,
                  fontSize: 16,
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  opacity: (!joinRoomId.trim() || !joinUsername.trim()) ? 0.5 : 1
                }}
              >REJOINDRE</button>
            </div>
          </div>
        )}

        {joinError && (
          <div style={{
            color: '#ff4444',
            backgroundColor: 'rgba(255, 68, 68, 0.1)',
            border: '1px solid rgba(255, 68, 68, 0.3)',
            borderRadius: 8,
            padding: 12,
            marginBottom: 15,
            fontSize: 14,
            textAlign: 'center'
          }}>
            {joinError}
          </div>
        )}

        {isJoiningRoom && !currentRoomId && (
          <div style={{
            fontSize: 16, 
            color: '#ff9800', 
            marginBottom: 20, 
            padding: '12px',
            backgroundColor: 'rgba(255, 152, 0, 0.1)',
            borderRadius: 8,
            border: '1px solid rgba(255, 152, 0, 0.3)',
            textAlign: 'center'
          }}>
            🔄 Tentative de rejoindre la room...
          </div>
        )}

        {/* Show room status if already joined */}
        {currentRoomId && (
          <div style={{
            fontSize: 16, 
            color: '#4CAF50', 
            marginBottom: 20, 
            padding: '12px',
            backgroundColor: 'rgba(76, 175, 80, 0.1)',
            borderRadius: 8,
            border: '1px solid rgba(76, 175, 80, 0.3)',
            textAlign: 'center'
          }}>
            ✅ Connecté à la room <strong>{currentRoomId}</strong>

            {/* Show players in room */}
            <div style={{ marginTop: 15 }}>
              <div style={{fontSize: 14, fontWeight: 'bold', marginBottom: 8}}>
                Joueurs connectés ({roomPlayers.length}) :
              </div>
              <div style={{
                backgroundColor: '#2a2a2a',
                borderRadius: 6,
                padding: 8,
                maxHeight: 100,
                overflowY: 'auto'
              }}>
                {roomPlayers.length === 0 ? (
                  <div style={{
                    color: '#ccc',
                    fontSize: 14,
                    fontStyle: 'italic'
                  }}>
                    Chargement des joueurs...
                  </div>
                ) : (
                  roomPlayers.map((player, index) => (
                    <div key={index} style={{
                      padding: '4px 8px',
                      borderBottom: index < roomPlayers.length - 1 ? '1px solid #444' : 'none',
                      color: player.isLeader ? '#FFD700' : '#fff'
                    }}>
                      {player.name} {player.isLeader && '👑'}
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>
        )}

        {/* Show START GAME button only if current user is the leader */}
        {isRoomLeader && currentRoomId && (
          <button
            onClick={onStartGame}
            style={{
              padding: '12px 32px',
              fontSize: 20,
              borderRadius: 10,
              background: '#ff6600',
              color: '#fff',
              border: 'none',
              fontWeight: 'bold',
              cursor: 'pointer',
              boxShadow: '0 2px 8px #0006',
              letterSpacing: 1,
              marginBottom: 15,
              width: '100%'
            }}
          >START GAME (Leader)</button>
        )}

        <button
          onClick={onClose}
          style={{
            position: 'absolute',
            top: 12,
            right: 18,
            background: 'transparent',
            color: '#FFD700',
            border: 'none',
            fontSize: 28,
            cursor: 'pointer',
            fontWeight: 'bold',
          }}
          title="Fermer"
        >×</button>
      </div>
    </div>
  );
};

