    setOpponentsScores({});
    setPenaltyNotification(null);
    setJoinError('');
    setIsJoiningRoom(false);
    setUrlJoinStatus('');
    setAutoJoinAttempted(false);
  };

  const savePlayerName = (playerName) => {
    setCurrentPlayerName(playerName);
    saveToLocalStorage(STORAGE_KEYS.LAST_USERNAME, playerName);
  };

  // Get opponents data for display
  const getOpponents = () => {
    return roomPlayers
      .filter(p => p.id !== currentPlayerId)
      .map(p => ({ 
        id: p.id, 
        name: p.name,
        score: opponentsScores[p.id] || 0,
        spectrum: opponentsSpectrums[p.id] || Array(10).fill(0)
      }));
  };

  return {
    // Modal state
    showCreateRoomModal,
    setShowCreateRoomModal,
    showJoinRoomModal,
    setShowJoinRoomModal,

    // Room creation state
    createRoomId,
    username,
    setUsername,
    userList,
    setUserList,

    // Room joining state
    joinRoomId,
    setJoinRoomId,
    joinUsername,
    setJoinUsername,
    isJoiningRoom,
    setIsJoiningRoom,
    joinError,
    setJoinError,

    // Current player state
    currentPlayerName,
    setCurrentPlayerName,

    // Room state
    currentRoomId,
    setCurrentRoomId,
    currentGameId,
    setCurrentGameId,
    currentPlayerId,
    setCurrentPlayerId,
    roomPlayers,
    setRoomPlayers,
    isRoomLeader,
    setIsRoomLeader,
    isMultiplayer,
    setIsMultiplayer,

    // URL navigation state
    urlRoomInfo,
    setUrlRoomInfo,
    autoJoinAttempted,
    setAutoJoinAttempted,
    urlJoinStatus,
    setUrlJoinStatus,

    // Game state
    eliminatedPlayers,
    setEliminatedPlayers,
    gameWinner,
    setGameWinner,
    multiplayerGameEnded,
    setMultiplayerGameEnded,
    waitingForRematch,
    setWaitingForRematch,

    // Opponents data
    opponentsSpectrums,
    setOpponentsSpectrums,
    opponentsScores,
    setOpponentsScores,

    // Notifications
    penaltyNotification,
    setPenaltyNotification,

    // Helper functions
    initializeOpponents,
    resetOpponents,
    resetMultiplayerState,
    savePlayerName,
    getOpponents
  };
};

import { useEffect } from 'react';
import { 
  parseHashUrl, 
  createUrlHash, 
  updateUrlHash, 
  clearUrlHash 
} from '../utils/gameHelpers';
import { getFromLocalStorage, saveToLocalStorage } from '../services/localStorageService';
import { STORAGE_KEYS } from '../utils/gameConstants';

/**
 * Hook to manage URL-based room navigation
 */
export const useURLNavigation = ({
  urlRoomInfo,
  setUrlRoomInfo,
  autoJoinAttempted,
  setAutoJoinAttempted,
  currentRoomId,
  currentPlayerName,
  isJoiningRoom,
  setJoinRoomId,
  setJoinUsername,
  setCurrentPlayerName,
  setIsJoiningRoom,
  setJoinError,
  setUrlJoinStatus,
  setShowJoinRoomModal,
  socketService
}) => {

  // Parse URL hash on component mount and setup hash change listener
  useEffect(() => {
    const getCurrentUrlInfo = () => {
      const hash = window.location.hash.slice(1); // Remove the # character
      return parseHashUrl(hash);
    };

    const urlInfo = getCurrentUrlInfo();
    setUrlRoomInfo(urlInfo);

    // Listen for hash changes
    const handleHashChange = () => {
      const newUrlInfo = getCurrentUrlInfo();
      setUrlRoomInfo(newUrlInfo);
      setAutoJoinAttempted(false); // Reset auto-join for new URL
    };

    window.addEventListener('hashchange', handleHashChange);

    return () => {
      window.removeEventListener('hashchange', handleHashChange);
    };
  }, [setUrlRoomInfo, setAutoJoinAttempted]);

  // Auto-join room from URL if present
  useEffect(() => {
    if (urlRoomInfo && !autoJoinAttempted && !currentRoomId && !isJoiningRoom) {
      setAutoJoinAttempted(true);

      const { roomId, playerName } = urlRoomInfo;

      // Set values for auto-join
      setJoinRoomId(roomId);
      if (playerName) {
        setJoinUsername(playerName);
        setCurrentPlayerName(playerName);
        saveToLocalStorage(STORAGE_KEYS.LAST_USERNAME, playerName);
      }

      // Automatically attempt to join the room
      console.log('Auto-joining room from URL:', { roomId, playerName });
      setIsJoiningRoom(true);
      setJoinError('');
      setUrlJoinStatus('joining');

      // Use the playerName from URL or the last saved username
      const nameToUse = playerName || getFromLocalStorage(STORAGE_KEYS.LAST_USERNAME, '');
      if (nameToUse) {
        setCurrentPlayerName(nameToUse);
        socketService.joinRoom(roomId, nameToUse);
      } else {
        // No username available, show join modal for user to enter name
        setIsJoiningRoom(false);
        setUrlJoinStatus('');
        setShowJoinRoomModal(true);
      }
    }
  }, [
    urlRoomInfo, 
    autoJoinAttempted, 
    currentRoomId, 
    isJoiningRoom,
    setAutoJoinAttempted,
    setJoinRoomId,
    setJoinUsername,
    setCurrentPlayerName,
    setIsJoiningRoom,
    setJoinError,
    setUrlJoinStatus,
    setShowJoinRoomModal,
    socketService
  ]);

  // Update URL when room changes
  useEffect(() => {
    if (currentRoomId && currentPlayerName) {
      const newHash = createUrlHash(currentRoomId, currentPlayerName);
      updateUrlHash(newHash);
    } else if (!currentRoomId && window.location.hash) {
      // Clear hash when leaving room
      clearUrlHash();
    }
  }, [currentRoomId, currentPlayerName]);

  return {
    // No additional state or functions needed to return
    // This hook manages URL navigation effects only
  };
};

import React from 'react';
import { createRoot } from 'react-dom/client';
import App from './containers/app';

const root = createRoot(document.getElementById('tetris'));
root.render(<App />);

/**
 * LocalStorage service for Tetris game
 * Provides safe read/write operations with error handling
 */

/**
 * Get an item from localStorage with default value fallback
 * @param {string} key - The localStorage key
 * @param {any} defaultValue - Default value if key doesn't exist or parsing fails
 * @returns {any} The parsed value or defaultValue
 */
export const getFromLocalStorage = (key, defaultValue) => {
  try {
    const item = localStorage.getItem(key);
    return item ? JSON.parse(item) : defaultValue;
  } catch (error) {
    console.error('Error reading from localStorage:', error);
    return defaultValue;
  }
};

/**
 * Save an item to localStorage
 * @param {string} key - The localStorage key
 * @param {any} value - The value to save
 */
export const saveToLocalStorage = (key, value) => {
  try {
    localStorage.setItem(key, JSON.stringify(value));
  } catch (error) {
    console.error('Error saving to localStorage:', error);
  }
};

/**
 * Remove an item from localStorage
 * @param {string} key - The localStorage key to remove
 */
export const removeFromLocalStorage = (key) => {
  try {
    localStorage.removeItem(key);
  } catch (error) {
    console.error('Error removing from localStorage:', error);
  }
};

/**
 * Clear all localStorage items (useful for reset)
 */
export const clearLocalStorage = () => {
  try {
    localStorage.clear();
  } catch (error) {
    console.error('Error clearing localStorage:', error);
  }
};

/**
 * Check if localStorage is available
 * @returns {boolean} True if localStorage is available
 */
export const isLocalStorageAvailable = () => {
  try {
    const testKey = '__test__';
    localStorage.setItem(testKey, 'test');
    localStorage.removeItem(testKey);
    return true;
  } catch (error) {
    console.warn('localStorage is not available:', error);
    return false;
  }
};

import { io } from 'socket.io-client';

class SocketService {
  constructor() {
    this.socket = null;
    this.isConnected = false;
  }

  connect() {
    if (this.socket) return;

    this.socket = io('http://localhost:3004', {
      autoConnect: true,
      transports: ['websocket'],
      reconnection: true, // Enable reconnection
      reconnectionAttempts: 10, // Try up to 10 times
      reconnectionDelay: 1000, // Start with 1s delay
      reconnectionDelayMax: 5000 // Max 5s delay
    });

    this.socket.on('connect', () => {
      console.log('Connected to server:', this.socket.id);
      this.isConnected = true;
    });

    this.socket.on('disconnect', () => {
      console.log('Disconnected from server');
      this.isConnected = false;
    });

    this.socket.on('connect_error', (error) => {
      console.error('Connection error:', error);
    });

    // --- Reconnection event listeners ---
    this.socket.on('reconnect_attempt', (attempt) => {
      console.warn(`Reconnection attempt #${attempt}`);
    });
    this.socket.on('reconnect', (attempt) => {
      console.info(`Successfully reconnected on attempt #${attempt}`);
    });
    this.socket.on('reconnect_failed', () => {
      console.error('Reconnection failed after maximum attempts');
    });
  }

  disconnect() {
    if (this.socket) {
      this.socket.disconnect();
      this.socket = null;
      this.isConnected = false;
    }
  }

  // Room management
  joinRoom(roomId, playerName, isCreator = false) {
    if (this.socket) {
      console.log('Sending join-room:', { roomId, playerName, isCreator });
      this.socket.emit('join-room', { roomId, playerName, isCreator });
    } else {
      console.error('Socket not connected when trying to join room');
    }
  }

  startGame(gameId) {
    if (this.socket) {
      this.socket.emit('start-game', { gameId });
    }
  }

  // Game events
  sendPlayerMove(playerId, move) {
    if (this.socket) {
      this.socket.emit('player-move', { playerId, move });
    }
  }

  sendPiecePlaced(playerId, piece, newBoard) {
    if (this.socket) {
      this.socket.emit('piece-placed', { playerId, piece, newBoard });
    }
  }

  sendLinesCleared(playerId, linesCleared, newBoard, newScore) {
    if (this.socket) {
      this.socket.emit('lines-cleared', { playerId, linesCleared, newBoard, newScore });
    }
  }

  sendGameOver(playerId) {
    if (this.socket) {
      this.socket.emit('game-over', { playerId });
    }
  }

  // Send board update to server (for spectrum calculation)
  sendBoardUpdate(playerId, board) {
    if (this.socket) {
      this.socket.emit('board-update', { playerId, board });
    }
  }

  // Send score update to server
  sendScoreUpdate(playerId, score) {
    if (this.socket) {
      this.socket.emit('score-update', { playerId, score });
    }
  }

  // Send pause state to server
  sendPauseState(playerId, isPaused) {
    if (this.socket) {
      this.socket.emit('pause-state', { playerId, isPaused });
    }
  }

  // Send player ready for rematch
  sendPlayerReady(playerId) {
    if (this.socket) {
      this.socket.emit('player-ready-rematch', { playerId });
    }
  }

  // Leave room (when going to lobby)
  leaveRoom(playerId) {
    if (this.socket) {
      this.socket.emit('leave-room', { playerId });
    }
  }

  // Request next piece from server (for synchronized multiplayer)
  requestNextPiece(playerId) {
    if (this.socket) {
      this.socket.emit('request-next-piece', { playerId });
    }
  }

  // Event listeners setup
  onRoomJoined(callback) {
    if (this.socket) {
      this.socket.on('room-joined', (data) => {
        console.log('Received room-joined event:', data);
        callback(data);
      });
    }
  }

  onJoinError(callback) {
    if (this.socket) {
      this.socket.on('join-error', (data) => {
        console.log('Received join-error event:', data);
        callback(data);
      });
    }
  }

  onGameStarted(callback) {
    if (this.socket) {
      this.socket.on('game-started', callback);
    }
  }

  onNextPiece(callback) {
    if (this.socket) {
      this.socket.on('next-piece', callback);
    }
  }

  onPlayerMove(callback) {
    if (this.socket) {
      this.socket.on('player-move', callback);
    }
  }

  onPlayerSpectrum(callback) {
    if (this.socket) {
      this.socket.on('player-spectrum', callback);
    }
  }

  onPenaltyLines(callback) {
    if (this.socket) {
      this.socket.on('penalty-lines', callback);
    }
  }

  onPlayerBoardUpdated(callback) {
    if (this.socket) {
      this.socket.on('player-board-updated', callback);
    }
  }

  onPlayerScoreUpdated(callback) {
    if (this.socket) {
      this.socket.on('player-score-updated', callback);
    }
  }

  onPlayerEliminated(callback) {
    if (this.socket) {
      this.socket.on('player-eliminated', (data) => {
        console.log('🔥 Received player-eliminated event:', data);
        callback(data);
      });
    }
  }

  onGameEnd(callback) {
    if (this.socket) {
      this.socket.on('game-end', (data) => {
        console.log('🏆 Received game-end event:', data);
        callback(data);
      });
    }
  }

  // Remove listeners
  off(event, callback) {
    if (this.socket) {
      this.socket.off(event, callback);
    }
  }

  removeAllListeners() {
    if (this.socket) {
      // Only remove game-specific events, keep connection events
      const eventsToRemove = [
        'room-joined',
        'join-error', 
        'game-started',
        'next-piece',
        'player-move',
        'player-spectrum',
        'penalty-lines',
        'player-board-updated',
        'player-score-updated',
        'player-eliminated',
        'game-end',
        'player-ready-rematch',
        'leave-room'
      ];

      eventsToRemove.forEach(event => {
        this.socket.off(event);
      });
    }
  }
}

